package com.tt.tournament.infrastructure.web

import org.springframework.web.bind.annotation.GetMapping
import org.springframework.web.bind.annotation.RestController
import org.thymeleaf.TemplateEngine
import org.thymeleaf.context.Context
import org.thymeleaf.templatemode.TemplateMode
import org.thymeleaf.templateresolver.ClassLoaderTemplateResolver
import org.xhtmlrenderer.pdf.ITextRenderer
import java.io.File
import java.io.FileOutputStream
import java.io.OutputStream


private const val IMAGE_BASE_64 =
    ""

@RestController
class ReportingController() {


    @GetMapping("/reports")
    fun getReport() {
        val parseThymeleafTemplate = parseThymeleafTemplate()
        generatePdfFromHtml(parseThymeleafTemplate)
    }

    private fun parseThymeleafTemplate(): String {
        val templateResolver = ClassLoaderTemplateResolver()
        templateResolver.suffix = ".html"
        templateResolver.templateMode = TemplateMode.HTML
        val templateEngine = TemplateEngine()
        templateEngine.setTemplateResolver(templateResolver)
        val context = Context()
        context.setVariable("base64Image", IMAGE_BASE_64)
        return templateEngine.process("receipt", context)
    }

    fun generatePdfFromHtml(html: String?) {
        val outputFolder = (System.getProperty("user.home") + File.separator).toString() + "thymeleaf2.pdf"
        println(System.getProperty("user.home"))
        val outputStream: OutputStream = FileOutputStream(outputFolder)
        val renderer = ITextRenderer()
        renderer.setDocumentFromString(html)
        renderer.layout()
        renderer.createPDF(outputStream)
        outputStream.close()
    }

}
